import MovieCard from "./MovieCard";

function MoviesList ({Movies, title}) {

    return(
            <div style={{marginTop:"20px", marginLeft:"150px"}}>
              {
                Movies.filter(el=>el.title.toLowerCase().includes(title.toLowerCase().trim())).map((el,i)=><MovieCard movie={el} key={i}/>)
              }
            </div>
    )}

export default MoviesList;
